if (array_key_exists('USERCP_SHELL',$vbulletin->templatecache))
{
    $need_warning_link = false;
    $warning_group_list = unserialize($vbulletin->options['pm_warning_groups']);
    if (!empty($warning_group_list) AND is_member_of($vbulletin->userinfo, $warning_group_list))
    {
        $need_warning_link = true;
    }
    if (!empty($vbulletin->options['pm_warning_user_ids']))
    {
        $warning_user_ids = array_map('intval', (explode(',', $vbulletin->options['pm_warning_user_ids'])));
        if (in_array($vbulletin->userinfo['userid'], $warning_user_ids))
        {
            $need_warning_link = true;
        }
    }
    
    if ($need_warning_link)
    {
        $old_last_item_in_menu = '<tr><td class=\"$navclass[pm_editfolders]\" nowrap=\"nowrap\"><a class=\"smallfont\" href=\"private.php?" . $GLOBALS[\'vbulletin\']->session->vars[\'sessionurl\'] . "do=editfolders\">$vbphrase[edit_folders]</a></td></tr>';
        $new_last_item_in_menu = '<tr><td class=\"$navclass[pm_editfolders]\" nowrap=\"nowrap\"><a class=\"smallfont\" href=\"private.php?" . $GLOBALS[\'vbulletin\']->session->vars[\'sessionurl\'] . "do=editfolders\">$vbphrase[edit_folders]</a></td></tr>
                                  <tr><td class=\"$navclass[pm_newpm]\"       nowrap=\"nowrap\"><a class=\"smallfont\" href=\"private.php?" . $GLOBALS[\'vbulletin\']->session->vars[\'sessionurl\'] . "do=pmwarning\">$vbphrase[usercp_menu_pm_warning_edit]</a></td></tr>';
        $vbulletin->templatecache['USERCP_SHELL'] = str_replace($old_last_item_in_menu, $new_last_item_in_menu, $vbulletin->templatecache['USERCP_SHELL']);
    }
}

